configs.site.customCss:
div {
  background-color: rgba(255, 255, 255, 0) !important;
}
/* 新增固定背景层 */
body::before {
    content: "";
    position: fixed;      /* 固定在屏幕上 */
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://tgwj.nb6.qzz.io/file/1758185184281_IMG_5680.jpeg');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    z-index: -1;          /* 放在最底层 */
}

/* 让页面内容容器透明，显示背景 */
#app, .main, .page-wrapper, .content, footer {
    background: transparent !important;
}}
